import {
    Text,
    View,
    SafeAreaView,
    ScrollView,
    TouchableOpacity,
    Image,
    BackHandler,
    StyleSheet,
    Dimensions,
    Button,
    image,
    Modal,
    TextInput,
  } from "react-native";
  
import SimpleLineIcons from "react-native-vector-icons/SimpleLineIcons";
import Octicons from "react-native-vector-icons/Octicons";
import { Slider } from "react-native-range-slider-expo";
import CalendarPicker from "react-native-calendar-picker";
import moment from "moment";
import DateTimePicker from "@react-native-community/datetimepicker";
  import * as ImagePicker from 'expo-image-picker';
  import React, { useState, useEffect } from "react";
  import { Colors, Default, Fonts } from "../constants/styles";
  import Ionicons from "react-native-vector-icons/Ionicons";
  import { useTranslation } from "react-i18next";
  
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
  
const { width, height } = Dimensions.get("window");
const Checkbox = ({ label, onChange, checked }) => {
  return (
    <TouchableOpacity
      style={styles.checkboxContainer}
      onPress={onChange}
    >
      <Ionicons
        name={checked ? 'checkbox' : 'checkbox-outline'}
        size={24}
        color={checked ? 'black' : 'black'}
      />
      <Text style={styles.label}>{label}</Text>
    </TouchableOpacity>
  );
};

  const PayPalScreen = ({ navigation }) => {
    
    const [checkedValues, setCheckedValues] = useState([]);

    const handleCheckboxChange = (value) => {
      if (checkedValues.includes(value)) {
        setCheckedValues(checkedValues.filter((item) => item !== value));
      } else {
        setCheckedValues([...checkedValues, value]);
      }
    }

    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState('Category');
    const [showCustomOption, setShowCustomOption] = useState(false);
    const [customOption, setCustomOption] = useState('');

    const handleOptionSelect = (option) => {
      if (option === "Others") {
     
        setShowCustomOption(true); // Show the custom option TextInput
        setSelectedOption(option);
        setDropdownOpen(false);
      } else {
        setSelectedOption(option);
        setDropdownOpen(false); // Close the dropdown for other options
        setShowCustomOption(false); // Hide the custom option TextInput
      }
    };
    
    const [dropdownOpens, setDropdownOpens] = useState(false);
    const [selectedOptions, setSelectedOptions] = useState('Skill Level');
  
    
    const [dropdownOpensds, setDropdownOpensds] = useState(false);
    const [selectedOptionsds, setSelectedOptionsds] = useState('Days');

    
    const [dropdownOpensd, setDropdownOpensd] = useState(false);
    const [selectedOptionsd, setSelectedOptionsd] = useState('Visibility');
    

    const handleOptionSelects = (option) => {
      setSelectedOptions(option);
      setDropdownOpens(false);
    };
    const handleOptionSelectsd = (option) => {
      setSelectedOptionsd(option);
      setDropdownOpensd(false);
    };

    
    const handleOptionSelectsds = (option) => {
      setSelectedOptionsds(option);
      setDropdownOpensds(false);
    };

    
  const [checked, setChecked] = useState(false);
    const { t, i18n } = useTranslation();
    const [selectedValue, setSelectedValue] = useState('');

    const isRtl = i18n.dir() == "rtl";
  
    function tr(key) {
      return t(`payPalScreen:${key}`);
    }
    const [pricePerHour, setPricePerHour] = useState('');
    const [modalVisible, setModalVisible] = useState(false);
  
    const handlePriceChange = (price) => {
      if (/^\d*\.?\d{0,2}/.test(price)) { // validate input is a valid number with max 2 decimal places
        setPricePerHour(price);
        setModalVisible(false); // close modal after selection
      }
    }
    const backAction = () => {
      navigation.goBack();
      return true;
    };
    useEffect(() => {
      BackHandler.addEventListener("hardwareBackPress", backAction);
  
      return () =>
        BackHandler.removeEventListener("hardwareBackPress", backAction);
    }, []);
    const [email, setEmail] = useState();
    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
          mediaTypes: ImagePicker.MediaTypeOptions.All,
          allowsEditing: true,
          aspect: [4, 3],
          quality: 1,
        });
    
        if (!result.cancelled) {
          setImage(result.uri);
        }
      };
      const [timeModal, setTimeModal] = useState(false);
      const [time, setTime] = useState(new Date());
      const [selectedTime, setSelectedTime] = useState(false);
    
      const onTimeSelected = (event, value) => {
        setTimeModal(false);
        setTime(value);
        setSelectedTime(true);
      };
    
      const confirmTime = (time) => {
        return `${time.getHours()}:${time.getMinutes()} `;
      };
    
      const [value, setValue] = useState(0);

      const [date, setDate] = useState();
      const [calendarModal, setCalendarModel] = useState(false);
      const today = moment().format("YYYY-MM-DD");
      const [finalDate, setFinalDate] = useState();
    
      const handleConfirmCalendar = (date) => {
        setFinalDate(date);
      };
    
      const confirmDate = () => {
        let tempDate = date.toString().split(" ");
        return date !== "" ? `${tempDate[2]} ${tempDate[1]} ${tempDate[3]}` : "";
      };
    
    return (
      <SafeAreaView style={{ flex: 1, backgroundColor: Colors.extraLightGrey }}>
        <View
          style={{
            paddingVertical: Default.fixPadding * 1.2,
            flexDirection: isRtl ? "row-reverse" : "row",
            alignItems: "center",
            backgroundColor: Colors.primary,
            paddingHorizontal: Default.fixPadding * 2,
          }}
        >
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <Ionicons
              name={isRtl ? "arrow-forward" : "arrow-back"}
              size={25}
              color={Colors.white}
            />
          </TouchableOpacity>
          <Text
            style={{
              ...Fonts.SemiBold18white,
              marginHorizontal: Default.fixPadding * 1.2,
            }}
          >
            {("Add Skill")}
          </Text>
        </View>
  
        <ScrollView showsVerticalScrollIndicator={false}>
          
        <View style={{ alignItems: 'center', justifyContent: 'center', flexDirection: 'row' , marginTop:20 }}>
        <TouchableOpacity>
        <Ionicons name="ios-camera" size={80} color="grey" />
      </TouchableOpacity>
      {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
    </View>
          <View
            style={{
              marginHorizontal: Default.fixPadding * 2,
            //  marginVertical: Default.fixPadding * 2,
            }}
          >
           
           <Text   style={{
                color:Colors.grey,
                marginLeft:135
              }}>Upload Pictures</Text>
    <View 
              style={{
                ...Default.shadow,
                borderRadius: 10,
                backgroundColor: Colors.white,
                padding: Default.fixPadding * 1.5,
                flexDirection: isRtl ? "row-reverse" : "row",
                
              //  alignItems: "center",
                marginTop: Default.fixPadding * 3,
              }}
              
            >
              <View>
                <Ionicons
                name="ios-folder-open-outline"
                color={Colors.grey}
                size={20}
                style={{
                  flex: 0.7,
                }}
              />
              </View>
              <View >
      <TouchableOpacity
        onPress={() => setDropdownOpen(!dropdownOpen)}
        style={{
          flexDirection: 'row',
          alignItems: 'center',
        paddingRight:1,
          color:'grey',

        }}
      >
        <View  style={{
            flexDirection:"row",
            marginLeft:15,

          

              }}>
        <Text    style={{
          marginTop:3,
          color:'grey',

              }}>{selectedOption}</Text>
              <View   style={{
          color:'grey',
          
    position: 'absolute',
    marginLeft:290

              }}>
        <Ionicons name={dropdownOpen ? 'chevron-up' : 'chevron-down'} size={24} color='grey' />
        </View>
        </View>
      </TouchableOpacity>
</View>
</View>
      {dropdownOpen && (
        <View
        style={styles.dropdownsd}
        >
          
          <TouchableOpacity onPress={() => handleOptionSelect('Electrician ')}>
            <Text style={{ padding: 10 }}>Electrician </Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Tutoring ')}>
            <Text style={{ padding: 10 }}>Tutoring</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('BabySitting')}>
            <Text style={{ padding: 10 }}>BabySitting</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Cooking')}>
            <Text style={{ padding: 10 }}>Cooking</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Gardening')}>
            <Text style={{ padding: 10 }}>Gardening</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Technology Assistance')}>
            <Text style={{ padding: 10 }}> Technology Assistance</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Tailoring')}>
            <Text style={{ padding: 10 }}> Tailoring </Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Salon')}>
            <Text style={{ padding: 10 }}> Salon </Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Painting ')}>
            <Text style={{ padding: 10 }}> Painting </Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Plumber')}>
            <Text style={{ padding: 10 }}> Plumber </Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Arts And Crafts')}>
            <Text style={{ padding: 10 }}> Arts And Crafts</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => handleOptionSelect('Others')}>
            <Text style={{ padding: 10 }}> Others </Text>
          </TouchableOpacity>
          
        </View>
        
      )}
       {showCustomOption && (
          <View
            style={{
              ...Default.shadow,
              borderRadius: 10,
              backgroundColor: Colors.white,
              padding: Default.fixPadding * 1.5,
              flexDirection: isRtl ? "row-reverse" : "row",
              alignItems: "center",
              marginTop: Default.fixPadding * 3,
              textAlign: isRtl ? "right" : "left",
            }}
          >
            <TextInput
              value={customOption}
              onChangeText={(text) => setCustomOption(text)}
              placeholder="Enter your skill name"
              style={{ paddingHorizontal: 10 }}
            />
          </View>
        )}
            
           
             <View style={{  ...Default.shadow,
                borderRadius: 10,
                backgroundColor: Colors.white,
                padding: Default.fixPadding * 1.5,
                flexDirection: isRtl ? "row-reverse" : "row",
                alignItems: "center",
                marginTop: Default.fixPadding * 3,
                textAlign: isRtl ? "right" : "left", }}>
          
          <TouchableOpacity
            onPress={() => navigation.navigate("Locate")}
            style={{
              flexDirection: isRtl ? "row-reverse" : "row",
              alignItems: "center",
            }}
          >
              <SimpleLineIcons
              name="location-pin"
              size={18}
              color={Colors.grey}
              style={{
              //  flex: 0.6,
              }}
            />
            
            <View
              style={{
                flexDirection: isRtl ? "row-reverse" : "row",
                alignItems: "center",
              }}
            >
              
                
              <Text
                numberOfLines={2}
                style={{
                  ...Fonts.SemiBold14grey,
                  overflow: "hidden",
                  textAlign: isRtl ? "right" : "left",
                  paddingLeft:21
                }}
              >
            Location
              </Text>
            </View>
        
          </TouchableOpacity>
        </View>
            {/* <View
              style={{
                ...Default.shadow,
                borderRadius: 10,
                backgroundColor: Colors.white,
                padding: Default.fixPadding * 1.5,
                flexDirection: isRtl ? "row-reverse" : "row",
                alignItems: "center",
                marginTop: Default.fixPadding * 3,
              }}
            >
                
              <Ionicons
                name="options-outline"
                color={Colors.grey}
                size={20}
                style={{
                  flex: 0.7,
                }}
              />
              <TextInput
                placeholder={tr("Type")}
                placeholderTextColor={Colors.grey}
                selectionColor={Colors.primary}
                style={{
                  ...Fonts.Medium16Black,
                  flex: 9.3,
                  marginHorizontal: Default.fixPadding,
                  textAlign: isRtl ? "right" : "left",
                }}
              />
              
            </View> */}
               <View 
              style={{
                ...Default.shadow,
                borderRadius: 10,
                backgroundColor: Colors.white,
                padding: Default.fixPadding * 1.5,
                flexDirection: isRtl ? "row-reverse" : "row",
                
              //  alignItems: "center",
                marginTop: Default.fixPadding * 3,
              }}
              
            >
              <View>
                <Ionicons
                name="options-outline"
                color={Colors.grey}
                size={20}
                style={{
                  flex: 0.7,
                }}
              />
              </View>
              <View >
      <TouchableOpacity
        onPress={() => setDropdownOpens(!dropdownOpens)}
        style={{
          flexDirection: 'row',
          alignItems: 'center',
        paddingRight:1,
          color:'grey',

        }}
      >
        <View  style={{
            flexDirection:"row",
            marginLeft:15,

          

              }}>
        <Text    style={{
          marginTop:3,
          color:'grey',

              }}>{selectedOptions}</Text>
              <View   style={{
          color:'grey',
          
    position: 'absolute',
    marginLeft:290

              }}>
        <Ionicons name={dropdownOpens ? 'chevron-up' : 'chevron-down'} size={24} color='grey' />
        </View>
        </View>
      </TouchableOpacity>
</View>
</View>
      {dropdownOpens && (
        <View
        style={styles.dropdowns}
